package test.beast.evolution;

import beast.evolution.alignment.Alignment;
import beast.evolution.alignment.Sequence;
import beast.evolution.datatype.Codon;
import beast.evolution.tree.Tree;
import beast.util.StringUtils;
import beast.util.TreeParser;

/**
 * @author Walter Xie
 */
public class CodonTestData {

    public static final Codon codonUNIVERSAL = new Codon();

    // concatenate Codon.CODON_TRIPLETS to a string
    public static String getTriplets() {
        return StringUtils.concatenateToString(Codon.CODON_TRIPLETS);
    }

    // create a Sequence obj using Codon.codeMap
    public static Sequence getSeqCodeMap() {
        return new Sequence("codemap", codonUNIVERSAL.getCodeMap());
    }

    public static int[] getTestStates() {
        return new int[]{0, 20, 40, 60, 63, 64, 124};
    }


    public static Alignment getAlignment() {
        Sequence s1 = new Sequence("Human_Horai", "");
        Sequence s2 = new Sequence("Human_Arnason", "");
        Sequence s3 = new Sequence("Chimp_Horai", "");
        Sequence s4 = new Sequence("Chimp_Arnason", "");
        Sequence s5 = new Sequence("Gorilla_Horai", "");
        Sequence s6 = new Sequence("Gorilla_Arnason", "");

        Alignment data = new Alignment();
        data.initByName("sequence", s1, "sequence", s2, "sequence", s3, "sequence", s4, "sequence", s5, "sequence", s6,
                "dataType", "nucleotide"
        );
        return data;
    }

    public static Tree getStartingTree(Alignment data, String startingTree) {
        TreeParser t = new TreeParser();
        t.initByName("taxa", data, "newick", startingTree, "IsLabelledNewick", true);
        return t;
    }

}
